export default function QRCodePage(){
    var QR_CODE = new QRCode("qrcode", {
		width: 260,
		height: 260,
		colorDark: "#000000",
		colorLight: "#ffffff",
		correctLevel: QRCode.CorrectLevel.H,
	});

    const generateQRCode = (data) =>{
		QR_CODE.clear();
		QR_CODE.makeCode(data);
		// Show QRCode div
	}

    generateQRCode('hey');

    return (
        <div>
            <p>Code</p>
            <div class="qrcode" id="qrcode"></div>
        </div> 
    )
}
