import QRScan from 'qrscan'
import { useState } from "react"
export default function QRCamera(){
    const [result, setResult] = useState('');

    const onFind = (value) =>{
        setResult(value);
    }

    return (
        <div>
            <p>Camera</p>
            <QRScan onFind={() => onFind} />
            <p>{result}</p>
        </div>
    )
}
